import React, { Component } from 'react';

import axios from 'axios';

class AddQuay extends Component {
  constructor(props){
    super(props);

    this.state = {
      quayname: '',
      info: '',
      location: ''
    }
  }

  onChangeQuayname = (e) => {
    this.setState({
      quayname: e.target.value
    })
  }

  onChangeInfo = (e) => {
    this.setState({
      info: e.target.value
    })
  }

  onChangeLocation = (e) => {
    this.setState({
      location: e.target.value
    })
  }

  onSubmit = (e) => {
    e.preventDefault();

    const quay = {
      quayname: this.state.quayname,
      info: this.state.info,
      location: this.state.location
    }

    axios.post('http://localhost:5000/quays/add', quay)
      .then(response => alert(response.data))
      .catch(err => console.log(err));


  }

  render () {

    return (
      <div>
      <h3>Add Quay for Monitoring</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Quay Name </label>
          <input type="text"
              required
              className="form-control"
              onChange={this.onChangeQuayname} />
        </div>
        <div className="form-group"> 
          <label>Info </label>
          <input  type="text"
              required
              className="form-control"
              onChange={this.onChangeInfo}
              />
        </div>
        <div className="form-group">
          <label>Location (Coordinates - lat, long) </label>
          <input 
              type="text" 
              className="form-control"
              onChange={this.onChangeLocation}
              />
        </div>

        <div className="form-group">
          <input type="submit" value="Add Quay" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}



export default AddQuay;
