import React, { Component} from 'react';
import axios from 'axios';

class EditQuay extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quayname: '',
      info: '',
      location: ''
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/quays/' + this.props.match.params.id)
      .then(response => {
        this.setState({
          quayname: response.data.quayname,
          info: response.data.info,
          location: response.data.location
        })
      })
      .catch(err => console.log(err));
  }

  onChangeInfo = e => {
    this.setState({
      info: e.target.value
    })
  }

  onSubmit = e => {
    e.preventDefault();

    const quay = {
      quayname: this.state.quayname,
      info: this.state.info,
      location: this.state.location
    }

    console.log(quay);

    axios.post('http://localhost:5000/quays/update/' + this.props.match.params.id, quay)
      .then(res => alert("Quay Updated"))
      .catch(err => console.log(err));

  }


  render() {
    return (
      <div>
        <h3>Edit Quay</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Quay Name: </label>
            <select ref="userInput"
              required
              className="form-control"
              value={this.state.quayname}
              onChange={this.onChangeQuayname}>
                {
                  this.state.quays.map(quay => {
                    return <option
                      key={quay}
                      value={quay}>{quay}
                      </option>
                  })
                }
              </select>
          </div>
        </form>
      </div>
    )
  }
}
export default EditQuay;
