import React, { Component } from 'react';

import Aux from '../hoc/Aux';

class QuayStatus extends Component {
  constructor(props) {
    super(props);

    this.state = {
      safeToday: true
    }
  }

  status = () => {
    if(this.state.safeToday) {
      return "SAFE"
    } else {
      return "IN DANGER!"
    }
  }

  render() {


    return(
      <Aux>
        <h1>
          The quay wall is currently {this.status()}

        </h1>
      </Aux>
    );
  }
}

export default QuayStatus;
