  //     .then(response => {
  //       this.setState({tides: response.data})
  //       console.log(this.state.tides)})
  //     .catch(err => console.log(err));
  // }


  render() {

    return(
      <div>
        <p>
          These are the tides 
          {console.log(this.state.tides)}
        </p>
        <p>
          The {this.state.quayname} quay is currently {this.status()}
        </p>
      </div>

    );
  }
}

export default QuayStatus;
